	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(ConfigurationPeer::DATABASE_NAME);

		if ($this->isColumnModified(ConfigurationPeer::ID)) $criteria->add(ConfigurationPeer::ID, $this->id);
		if ($this->isColumnModified(ConfigurationPeer::NAME)) $criteria->add(ConfigurationPeer::NAME, $this->name);
		if ($this->isColumnModified(ConfigurationPeer::BRANCH)) $criteria->add(ConfigurationPeer::BRANCH, $this->branch);
		if ($this->isColumnModified(ConfigurationPeer::CONTENT)) $criteria->add(ConfigurationPeer::CONTENT, $this->content);
		if ($this->isColumnModified(ConfigurationPeer::FOOTER)) $criteria->add(ConfigurationPeer::FOOTER, $this->footer);
		if ($this->isColumnModified(ConfigurationPeer::FILE)) $criteria->add(ConfigurationPeer::FILE, $this->file);
		if ($this->isColumnModified(ConfigurationPeer::ALLIANCE)) $criteria->add(ConfigurationPeer::ALLIANCE, $this->alliance);
		if ($this->isColumnModified(ConfigurationPeer::GIFTCERT)) $criteria->add(ConfigurationPeer::GIFTCERT, $this->giftcert);
		if ($this->isColumnModified(ConfigurationPeer::SCDISCOUNT)) $criteria->add(ConfigurationPeer::SCDISCOUNT, $this->scdiscount);
		if ($this->isColumnModified(ConfigurationPeer::ITEMDISCOUNT)) $criteria->add(ConfigurationPeer::ITEMDISCOUNT, $this->itemdiscount);
		if ($this->isColumnModified(ConfigurationPeer::DISCOUNT)) $criteria->add(ConfigurationPeer::DISCOUNT, $this->discount);
		if ($this->isColumnModified(ConfigurationPeer::DEMO)) $criteria->add(ConfigurationPeer::DEMO, $this->demo);
		if ($this->isColumnModified(ConfigurationPeer::POINTS)) $criteria->add(ConfigurationPeer::POINTS, $this->points);
		if ($this->isColumnModified(ConfigurationPeer::TRANSDATE)) $criteria->add(ConfigurationPeer::TRANSDATE, $this->transdate);
		if ($this->isColumnModified(ConfigurationPeer::TRANSON)) $criteria->add(ConfigurationPeer::TRANSON, $this->transon);
		if ($this->isColumnModified(ConfigurationPeer::ASSISTANT)) $criteria->add(ConfigurationPeer::ASSISTANT, $this->assistant);
		if ($this->isColumnModified(ConfigurationPeer::WEIGHTING)) $criteria->add(ConfigurationPeer::WEIGHTING, $this->weighting);
		if ($this->isColumnModified(ConfigurationPeer::JOBORDER)) $criteria->add(ConfigurationPeer::JOBORDER, $this->joborder);

		return $criteria;
	}

