  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseConfiguration:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseConfiguration::%s', $method));
    }

    array_unshift($arguments, $this);

