		if ($this->isColumnModified(ReceiptPeer::COMMENTS)) $criteria->add(ReceiptPeer::COMMENTS, $this->comments);
		if ($this->isColumnModified(ReceiptPeer::CUSTOMER)) $criteria->add(ReceiptPeer::CUSTOMER, $this->customer);
