	 * @param      Purchaseorder $l Purchaseorder
	 * @return     void
	 * @throws     PropelException
	 */
	public function addPurchaseorder(Purchaseorder $l)
	{
		if ($this->collPurchaseorders === null) {
			$this->initPurchaseorders();
		}
		if (!in_array($l, $this->collPurchaseorders, true)) { // only add it if the **same** object is not already associated
			array_push($this->collPurchaseorders, $l);
			$l->setVendorRelatedByVendor($this);
		}
	}

	/**
	 * Clears out the collIssuances collection (array).
	 *
	 * This does not modify the database; however, it will remove any associated objects, causing
	 * them to be refetched by subsequent calls to accessor method.
	 *
	 * @return     void
	 * @see        addIssuances()
	 */
	public function clearIssuances()
	{
		$this->collIssuances = null; // important to set this to NULL since that means it is uninitialized
	}

	/**
	 * Initializes the collIssuances collection (array).
	 *
	 * By default this just sets the collIssuances collection to an empty array (like clearcollIssuances());
	 * however, you may wish to override this method in your stub class to provide setting appropriate
	 * to your application -- for example, setting the initial array to the values stored in database.
	 *
	 * @return     void
	 */
	public function initIssuances()
	{
		$this->collIssuances = array();
	}

	/**
	 * Gets an array of Issuance objects which contain a foreign key that references this object.
	 *
	 * If this collection has already been initialized with an identical Criteria, it returns the collection.
	 * Otherwise if this Vendor has previously been saved, it will retrieve
	 * related Issuances from storage. If this Vendor is new, it will return
	 * an empty collection or the current collection, the criteria is ignored on a new object.
	 *
	 * @param      PropelPDO $con
	 * @param      Criteria $criteria
	 * @return     array Issuance[]
	 * @throws     PropelException
	 */
	public function getIssuances($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(VendorPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collIssuances === null) {
			if ($this->isNew()) {
			   $this->collIssuances = array();
			} else {

				$criteria->add(IssuancePeer::PURCHASEORDER, $this->id);

				IssuancePeer::addSelectColumns($criteria);
				$this->collIssuances = IssuancePeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(IssuancePeer::PURCHASEORDER, $this->id);

				IssuancePeer::addSelectColumns($criteria);
				if (!isset($this->lastIssuanceCriteria) || !$this->lastIssuanceCriteria->equals($criteria)) {
					$this->collIssuances = IssuancePeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastIssuanceCriteria = $criteria;
		return $this->collIssuances;
	}

	/**
	 * Returns the number of related Issuance objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related Issuance objects.
	 * @throws     PropelException
	 */
	public function countIssuances(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(VendorPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collIssuances === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(IssuancePeer::PURCHASEORDER, $this->id);

				$count = IssuancePeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(IssuancePeer::PURCHASEORDER, $this->id);

				if (!isset($this->lastIssuanceCriteria) || !$this->lastIssuanceCriteria->equals($criteria)) {
					$count = IssuancePeer::doCount($criteria, $con);
				} else {
					$count = count($this->collIssuances);
				}
			} else {
				$count = count($this->collIssuances);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a Issuance object to this object
	 * through the Issuance foreign key attribute.
	 *
	 * @param      Issuance $l Issuance
	 * @return     void
	 * @throws     PropelException
	 */
	public function addIssuance(Issuance $l)
	{
		if ($this->collIssuances === null) {
			$this->initIssuances();
		}
		if (!in_array($l, $this->collIssuances, true)) { // only add it if the **same** object is not already associated
			array_push($this->collIssuances, $l);
			$l->setVendor($this);
		}
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collPurchaseorders) {
				foreach ((array) $this->collPurchaseorders as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collIssuances) {
				foreach ((array) $this->collIssuances as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collPurchaseorders = null;
		$this->collIssuances = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseVendor:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseVendor::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BaseVendor
